import axios from 'axios';
import React from 'react';
import '../client.css'

function CreateProject() {
    state = {
        project_title: "",
        owner: "",
        starting_date: "",
        expected_duration: ""
    }
    const handleInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    saveStudent = async (e) => {
        e.preventDefault();
        const res = await axios.post('')
    }
    return (
        <div className='create_project'>
            <div className="yellow-block"></div>
            <div className="blue-block"></div>
            <div className="create-form">
                <form>
                    <h3>Project Details</h3>
                    <div className="row">
                        <div className="col-md-12">
                            <label>Project Title</label><br />
                            <input type="text" name='project_title' onChange={this.handleInput} value={this.state.project_title} required />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-12">
                            <label>Owner</label><br />
                            <input type="text" name='owner' onChange={this.handleInput} value={this.state.owner} required />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-6">
                            <label>Starting Date</label><br />
                            <input type="date" name='starting_date' onChange={this.handleInput} value={this.state.starting_date} required />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-8">
                            <label>Expected Duration</label><br />
                            <input type="number" name='expected_duration' placeholder='Number of Days' onChange={this.handleInput} value={this.state.expected_duration} required />
                        </div>
                        <div className="col-md-4">
                            <button type='submit' className='save-btn'>Save</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default CreateProject;
