import React, { useState } from 'react';
import './client.css';

function ClientDashboard() {
    const [state, setState]= useState({msg:"Hi"});
    const handleClick=()=>{
    
        // Changing state
        this.setState({msg : 'Welcome to the React world!'})
      }
    return (
        <div className='container'>
            <header>
                <div className="header-content">
                    Welcome Raghav, Let's Get Started...
                    <img src="dist/img/HeaderIcon-1.png" alt="Icon 1" className='icon-1' />
                    <img src="dist/img/HeaderIcon-2.png" alt="Icon 1" className='icon-2' />
                </div>
                <div className="home-bar">
                    Home
                </div>
            </header>
            <aside className="sidenav">
                <img src="dist/img/PrakriaLogo_Final.png" alt="Prakria Direct Logo" className='Logo' />
                <button className='create-btn'>Create a project</button>
                <nav className='nav-menu'>
                    <ul>
                        <li onClick={this.handleClick}>Projects</li>
                        <li>Brands</li>
                        <li>Files</li>
                        <li>Add a Resource</li>
                        <li>Account</li>
                        <li>Logout</li>
                    </ul>
                </nav>
            </aside>
<div className="content-part">
    {this.state.msg}
</div>
            <aside className="control-sidebar control-sidebar-dark">
            </aside>
        </div>
    );
}

export default ClientDashboard;
